<template>        <el-row style="margin-top: 20px;">            <el-col type="flex" :span="6" v-for="(c,index) in campaigns" :key="index">                <el-card style="margin: 5px;" class="box-card">                    <div slot="header">                        <h2>{{ c.name }}</h2>                        <p class="small-margin">{{ c.type }}</p>                    </div>                    <div class="text item card-body">                        <el-row :gutter="20">                            <el-col :span="12">                                <div class="grid-content">                                    <p>                                        APPROVED POSTS                                    </p>                                    <p class="text-bold-big small-margin">                                        {{ c.approved_posts }}                                    </p>                                </div>                            </el-col>                            <el-col :span="12">                                <div class="grid-content">                                    <p>                                        INFLUENCES                                    </p>                                    <p class="text-bold-big small-margin">                                        {{ c.num_of_artists }}                                    </p>                                </div>                            </el-col>                        </el-row>                        <el-row :gutter="20" style="margin-top: 20px;">                            <el-col :span="12">                                <div class="grid-content">                                    <p>                                        LIKES                                    </p>                                    <p class="text-bold-big small-margin">                                        {{ c.approved_likes }}                                    </p>                                </div>                            </el-col>                            <el-col :span="12">                                <div class="grid-content">                                    <p>                                        COMMENTS                                    </p>                                    <p class="text-bold-big small-margin">                                        {{ c.approved_comments }}                                    </p>                                </div>                            </el-col>                        </el-row>                        <div class="grey-text">                            {{ Math.trunc((c.days_passed / c.days_total) * 100)}}% ({{ c.days_passed }}/{{ c.days_total }} days)                        </div>                    </div>                    <div class="card-bottom">                        <div class="bottom clearfix">                            <el-button type="text" class="hide-reject-button" @click="deleteCard(index)">HIDE                            </el-button>                            <el-button type="text" class="show-button">SHOW DETAILS</el-button>                        </div>                    </div>                </el-card>            </el-col>        </el-row></template><script>    import mock_dash from './dashboard_mock.json'    export default {        data() {            return {                campaigns: mock_dash.campaigns            }        },        methods: {            deleteCard(index) {                this.campaigns.splice(index, 1);            }        }    }</script>