<template>    <el-main>        <el-card style="padding: 20px;" shadow="always">            Name        </el-card>        <el-row style="margin-top: 20px">            <el-button-group>                <el-button>INSTAGRAM</el-button>                <el-button>YOUTUBE</el-button>                <el-button>FACEBOOK</el-button>                <el-button>TWITTER</el-button>            </el-button-group>        </el-row>        <el-tabs style="margin-top: 20px" v-model="activeName" @tab-click="handleClick">            <el-tab-pane label="PENDING" name="first">                <el-row style="margin-top: 20px">                    <el-pagination                            background                            layout="prev, pager, next"                            :total="1000" style="float:right">                    </el-pagination>                    <span style="float: left;">{{ medias.length }} items in total</span>                </el-row>                <el-row style="margin-top: 20px;">                    <el-col type="flex" :span="6" v-for="(o, index) in medias" :key="index">                        <el-card style="margin: 5px;" class="box-card">                            <div slot="header">                                <h2>{{ o.author_username }}</h2>                                <a :href="o.link">instagram</a>                            </div>                            <img :src="o.pic" class="image" width="430">                            <div style="padding: 14px;">                                <el-row :gutter="20" style="margin-top: 20px;">                                    <el-col :span="12">                                        <div class="grid-content">                                            <p>                                                LIKES                                            </p>                                            <p class="text-bold-big small-margin">                                                {{o.likes_number}}                                            </p>                                        </div>                                    </el-col>                                    <el-col :span="12">                                        <div class="grid-content">                                            <p>                                                COMMENTS                                            </p>                                            <p class="text-bold-big small-margin">                                                {{ o.comments_number }}                                            </p>                                        </div>                                    </el-col>                                </el-row>                                <hr>                                <div class="bottom clearfix">                                    <el-button type="text" @click="addRejected(o)" class="hide-reject-button">REJECT</el-button>                                    <el-button type="text" @click="addApproved(o)" class="show-button">APPROVE</el-button>                                </div>                            </div>                        </el-card>                    </el-col>                </el-row>            </el-tab-pane>            <el-tab-pane v-model="approved" label="APPROVED" name="second">                <el-row style="margin-top: 20px">                    <el-pagination                            background                            layout="prev, pager, next"                            :total="1000" style="float:right">                    </el-pagination>                    <span style="float: left;">{{ approved.length }} items in total</span>                </el-row>                <el-row style="margin-top: 20px;">                    <el-col type="flex" :span="6" v-for="(o, index) in approved" :key="index">                        <el-card style="margin: 5px;" class="box-card">                            <div slot="header">                                <h2>{{ o.author_username }}</h2>                                <a :href="o.link">instagram</a>                            </div>                            <img :src="o.pic" class="image" width="430">                            <div style="padding: 14px;">                                <el-row :gutter="20" style="margin-top: 20px;">                                    <el-col :span="12">                                        <div class="grid-content">                                            <p>                                                LIKES                                            </p>                                            <p class="text-bold-big small-margin">                                                {{o.likes_number}}                                            </p>                                        </div>                                    </el-col>                                    <el-col :span="12">                                        <div class="grid-content">                                            <p>                                                COMMENTS                                            </p>                                            <p class="text-bold-big small-margin">                                                {{ o.comments_number }}                                            </p>                                        </div>                                    </el-col>                                </el-row>                                <hr>                                <div class="bottom clearfix">                                    <el-button type="text" @click="addRejected(o)" class="hide-reject-button">REJECT</el-button>                                </div>                            </div>                        </el-card>                    </el-col>                </el-row>            </el-tab-pane>            <el-tab-pane v-model="rejected" label="REJECTED" name="third">                <el-row style="margin-top: 20px">                    <el-pagination                            background                            layout="prev, pager, next"                            :total="1000" style="float:right">                    </el-pagination>                    <span style="float: left;">{{ rejected.length }} items in total</span>                </el-row>                <el-row style="margin-top: 20px;">                    <el-col type="flex" :span="6" v-for="(o, index) in rejected" :key="index">                        <el-card style="margin: 5px;" class="box-card">                            <div slot="header">                                <h2>{{ o.author_username }}</h2>                                <a :href="o.link">instagram</a>                            </div>                            <img :src="o.pic" class="image" width="430">                            <div style="padding: 14px;">                                <el-row :gutter="20" style="margin-top: 20px;">                                    <el-col :span="12">                                        <div class="grid-content">                                            <p>                                                LIKES                                            </p>                                            <p class="text-bold-big small-margin">                                                {{o.likes_number}}                                            </p>                                        </div>                                    </el-col>                                    <el-col :span="12">                                        <div class="grid-content">                                            <p>                                                COMMENTS                                            </p>                                            <p class="text-bold-big small-margin">                                                {{ o.comments_number }}                                            </p>                                        </div>                                    </el-col>                                </el-row>                                <hr>                                <div class="bottom clearfix">                                    <el-button type="text" @click="addApproved(o)" class="show-button">APPROVE</el-button>                                </div>                            </div>                        </el-card>                    </el-col>                </el-row>            </el-tab-pane>        </el-tabs>    </el-main></template><script>    import mock_det from './details_mock.json'    export default {        data() {            return {                rejected:[],                approved:[],                medias: mock_det.medias,                activeName: 'first'            };        },        methods: {            addRejected(index){                this.rejected.push(index);                this.approved.splice(index, 1);            },            addApproved(index){                this.approved.push(index);                this.rejected.splice(index, 1);            },            handleClick(tab, event) {                console.log(tab, event);            }        }    };</script><style></style>