<template>    <el-main>        <el-card style="padding: 20px;" shadow="always">            Name        </el-card>        <el-row style="margin-top: 20px">            <el-button-group>                <el-button v-for="filter in filters" :key="filter" @click="setFilterBy(filter)">                    {{ filter.toUpperCase() }}                </el-button>            </el-button-group>        </el-row>        <el-tabs style="margin-top: 20px" v-model="activeName">            <el-tab-pane v-for="(type, index) in cardStatusTypes" :key="index" :v-model="type.name"                         :label="type.name.toUpperCase()" :name="type.name">                <el-row style="margin-top: 20px">                    <el-pagination                            background                            layout="prev, pager, next"                            :total="1000" style="float:right">                    </el-pagination>                    <span style="float: left;">{{ cards.length }} items in total</span>                </el-row>                <el-row style="margin-top: 20px;">                    <el-col type="flex" :span="6" v-for="(o, index) in cards" :key="index">                        <el-card style="margin: 5px;" class="box-card">                            <div slot="header">                                <h2>{{ o.author_username }}</h2>                                <a :href="o.link">{{ o.social_network }}</a>                            </div>                            <img :src="o.pic" class="image" width="430">                            <div style="padding: 14px;">                                <el-row :gutter="20" style="margin-top: 20px;">                                    <el-col :span="12">                                        <div class="grid-content">                                            <p>                                                LIKES                                            </p>                                            <p class="text-bold-big small-margin">                                                {{o.likes_number}}                                            </p>                                        </div>                                    </el-col>                                    <el-col :span="12">                                        <div class="grid-content">                                            <p>                                                COMMENTS                                            </p>                                            <p class="text-bold-big small-margin">                                                {{ o.comments_number }}                                            </p>                                        </div>                                    </el-col>                                </el-row>                                <hr>                                <div class="bottom clearfix">                                    <el-button v-for="(action, i) in getCardActions(type.actions)" :key="i"                                               type="text" @click="action.cb(index)" :class="action.class">                                        {{ action.name.toUpperCase() }}                                    </el-button>                                </div>                            </div>                        </el-card>                    </el-col>                </el-row>            </el-tab-pane>        </el-tabs>    </el-main></template><script>    import {medias as pending} from './details_mock.json'    export default {        data() {            return {                filterBy: null,                rejected: [],                approved: [],                pending,                activeName: 'pending',                allCardActions: [                    {                        name: 'approve',                        cb: this.addApproved,                        class: 'show-button'                    },                    {                        name: 'reject',                        cb: this.addRejected,                        class: 'hide-reject-button'                    }                ],                cardStatusTypes: [                    {                        name: 'pending',                        actions: ['approve', 'reject']                    },                    {                        name: 'approved',                        actions: ['reject']                    },                    {                        name: 'rejected',                        actions: ['approve']                    }                ],                filters: ['Instagram', 'Youtube', 'Facebook', 'Twitter']            };        },        computed: {            cards() {                let cards = this[this.activeName];                if (this.filterBy !== null) cards = this.filterCardsByNetwork(cards, this.filterBy)                return cards;            }        },        methods: {            setFilterBy(networkName) {                if (this.filterBy === networkName) this.filterBy = null;                else this.filterBy = networkName;            },            filterCardsByNetwork(cards, networkName) {                return cards.filter(card => card.social_network === networkName);            },            addRejected(index) {                this.moveElement(this.rejected, index);            },            addApproved(index) {                this.moveElement(this.approved, index);            },            moveElement(to, index) {                to.push(this[this.activeName].splice(index, 1)[0])            },            getCardActions(actions) {                return this.allCardActions.filter(action => actions.indexOf(action.name) !== -1)            }        }    };</script>